import React, { useEffect, useState } from 'react'
// import Container from 'react-bootstrap/Container'
// import Row from 'react-bootstrap/Row'
// import Col from 'react-bootstrap/Col'
import { useDispatch, useSelector } from 'react-redux'
import {
    selectGrid,
    updateGridAndNeighbors,
    initializeGridAndNeighbors
 } from './gridSlice'
import Cell from './Cell'

export default function Grid() {
    const initialGrid = useSelector(selectGrid)
    const dispatch = useDispatch()
    // initialization
    if (initialGrid.length === 0) {
        dispatch(initializeGridAndNeighbors())
    }

    useEffect(()=>{
        const timer = setTimeout(
            () => {
                console.log("enter effect")
                dispatch(updateGridAndNeighbors())},
            5000
        );
        return () => clearTimeout(timer);
    });

    const grid = useSelector(selectGrid)

    return (
        <div className="container"> 
            {grid.map(function(row, rowId) {
                return (
                    <div className="row">
                    {row.map(
                        (val, colId)=> <Cell pos={[rowId, colId]}/>
                    )}
                    </div>
                    )
            })}
        </div>
    )
}
