import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {toggleCell, selectGrid} from './gridSlice'

// import Col from 'react-bootstrap/Col'

export default function Cell(props) {
    const dispatch = useDispatch()
    const grid = useSelector(selectGrid)
    const toggleLifeStatus = () => {
        // className = "col"
        // if (props.life === 1) {
        //     className += ' dead'
        // } else {
        //     className += ' living'
        // }
        dispatch(toggleCell(props.pos))
        console.log(props.life)
    }

    let className = ""
    function addLifeStatus() {
        // console.log("toggleeee")
        className = "col"
        if (grid[props.pos[0]][props.pos[1]] === 1) {
            className += ' living'
        } else {
            className += ' dead'
        }
    }
    addLifeStatus();

    return (
        <div className={className} onClick={toggleLifeStatus}></div>
    )
}
