import React, {useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {
    toggleGame,
    stopGame,
 } from './gameStateSlice'
import {
    selectCount,
    resetGrid
 } from './gridSlice'

export default function GameButton() {
    const count = useSelector(selectCount)
    const dispatch = useDispatch()
    // let state = "start"
    const [state, setState] = useState("start")

    function toggleState() {
        dispatch(toggleGame())
        if (state === "start") {
            return "pause"
        } 
        return "start"
    }

    function resetState() {
        // state = "start"
        dispatch(stopGame())
        dispatch(resetGrid())
        return "start"
    }

    // if (count === 0) {
    //     setState("start")
    //     dispatch(stopGame())
    // }

    return (
        <div>
            <button onClick={() => setState(toggleState())}>{state}</button>
            <button onClick={() => setState(resetState())}>Reset</button>
        </div>
    )
}
