import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {toggleCell,
    updateGridAndNeighbors,
    selectGrid} from './gridSlice'

// import Col from 'react-bootstrap/Col'

export default function Cell(props) {
    const dispatch = useDispatch()
    const grid = useSelector(selectGrid)
    // const toggleLifeStatus = () => {
    //     dispatch(toggleCell(props.pos))
    // }

    let className = ""
    function addLifeStatus() {
        // console.log("toggleeee")
        className = "col"
        if (grid[props.pos[0]][props.pos[1]] === 1) {
            className += ' living'
        } else {
            className += ' dead'
        }
    }
    addLifeStatus();

    setTimeout(dispatch(updateGridAndNeighbors), 10000)

    return (
        <div className={className} onClick={() => dispatch(toggleCell(props.pos))}>
        </div>
    )
}
