import { createSlice } from '@reduxjs/toolkit';

export const gridSlice = createSlice({
    name:'size',
    initialState:{
        width: 0,
        height: 0,
        grid: [],
        livingNeighbors:[],
    },
    reducers: {
        setSize: (state, action) => action.payload
    }
});

export const {setSize} = gridSlice.actions;
export const selectWidth = state => state.size.width;
export const selectHeight = state => state.size.height;

export default gridSlice.reducer;
