import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { selectCount, selectStage } from '../grid/gridSlice';
import { stopGame } from '../buttons/gameStateSlice';
import './counter.css';

export default function Counter() {
    const count = Number(useSelector(selectCount));
    const stage = Number(useSelector(selectStage));
    const dispatch = useDispatch();
    let countWord = '';
    let stageWord = '';
    if (count === 0) {
        countWord = 'No living cell :(';
        dispatch(stopGame());
    } else if (count === 1) {
        countWord = '1 living cell :|';
    } else {
        countWord = count + ' living cells :)';
    }
    stageWord = 'stage: ' + stage;

    return (
        <div className="counterRow">
            <span className="counterItem">{countWord}</span>
            <span className="counterItem">{stageWord}</span>
        </div>
    );
}
