import { createSlice } from '@reduxjs/toolkit';

export const sizeSlice = createSlice({
    name:'size',
    initialState:{
        width: 50,
        height: 50,
    },
    reducers: {
        setSize: (state, action) => action.payload
    }
});

export const {setSize} = counterSlice.actions;
export const selectWidth = state => state.size.width;
export const selectHeight = state => state.size.height;

export default sizeSlice.reducer;
