import React, {useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {
    toggleGame,
    stopGame,
    selectPlay,
    selectStart,
 } from './gameStateSlice'
import {
    resetGrid,
    selectCount,
    updateGridAndNeighbors,
    clearGrid,
 } from './gridSlice'
import Button from 'react-bootstrap/Button'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
    faPlay,
    faPause,
    faForward,
    faTimes,
    faRandom,
 } from '@fortawesome/free-solid-svg-icons'



export default function GameButton() {
    const dispatch = useDispatch()
    const play = useSelector(selectPlay)
    const start = useSelector(selectStart)
    const count = useSelector(selectCount)
    const [playSign, setPlaySign] = useState(faPlay)

    if (count === 0) {
        setPlaySign(faPlay)
    }

    const toggleState= () => {
        if (count > 0) {
            dispatch(toggleGame())
            if (playSign === faPlay) {
                setPlaySign(faPause)
            } else {
                setPlaySign(faPlay)
            }
        }
    }

    const resetState = () => {
        // state = "start"
        dispatch(stopGame())
        dispatch(resetGrid())
        setPlaySign(faPlay)
    }

    const handleNext = () => {
        if (!start) {
            dispatch(updateGridAndNeighbors())
        }
    }

    const handleClear = () => {
        dispatch(stopGame())
        dispatch(clearGrid())
        setPlaySign(faPlay)
    }

    return (
        <div className="gameButtonRow">
            <Button className="gameStateButton" onClick={ toggleState }>
                <FontAwesomeIcon className="fontAwesomeIcon" icon={playSign} />{play}
            </Button>
            <Button className="gameStateButton" onClick={handleNext}>
                <FontAwesomeIcon className="fontAwesomeIcon" icon={faForward} />Next
            </Button>
            <Button className="gameStateButton" onClick={resetState}>
                <FontAwesomeIcon className="fontAwesomeIcon" icon={faRandom} />Reset
            </Button>
            <Button className="gameStateButton" onClick={handleClear}>
                <FontAwesomeIcon className="fontAwesomeIcon" icon={faTimes} />Clear
            </Button>
        </div>
    )
}
