import React, {useState} from 'react'
import reactCSS from 'reactcss'
import { SketchPicker } from 'react-color'
import { useDispatch, useSelector } from 'react-redux'
import {
    setDeadColor,
    selectDeadColor,
    setLivingColor,
    selectLivingColor,
} from './colorSlice'
import {
    selectStart
} from './gameStateSlice'


export default function Color() {
    const dispatch = useDispatch()
    const livingColor = useSelector(selectLivingColor)
    const deadColor = useSelector(selectDeadColor)
    const start = useSelector(selectStart)
    const [displayLivingPicker, setLivingDisplay] = useState(false)
    const [displayDeadPicker, setDeadDisplay] = useState(false)

    const styles = reactCSS({
        'default': {
          living: {
            width: '36px',
            height: '14px',
            borderRadius: '2px',
            background: `rgba(${ livingColor.r }, ${ livingColor.g }, ${ livingColor.b }, ${ livingColor.a })`,
          },
          dead: {
            width: '36px',
            height: '14px',
            borderRadius: '2px',
            background: `rgba(${ deadColor.r }, ${ deadColor.g }, ${ deadColor.b }, ${ deadColor.a })`,
          },
          swatch: {
            padding: '5px',
            background: '#fff',
            borderRadius: '1px',
            boxShadow: '0 0 0 1px rgba(0,0,0,.1)',
            display: 'inline-block',
            cursor: 'pointer',
          },
          popover: {
            position: 'absolute',
            zIndex: '2',
          },
          cover: {
            position: 'fixed',
            top: '0px',
            right: '0px',
            bottom: '0px',
            left: '0px',
          },
        },
      });

    const handleLivingClick = () => {
        if (!start) {
            setLivingDisplay(!displayLivingPicker)
        }
    };

    const handleDeadClick = () => {
        if (!start) {
            setDeadDisplay(!displayDeadPicker)
        }
    };

    const handleLivingClose = () => {
        if (!start) {
            setLivingDisplay(false)
        }
    };

    const handleDeadClose = () => {
        if (start) {
            setDeadDisplay(false)
        }
    };

    const handleLivingChange = (color) => {
        if (!start) {
            dispatch(setLivingColor(color.rgb))
        }
    };

    const handleDeadChange = (color) => {
        if (!start) {
            dispatch(setDeadColor(color.rgb))
        }
    };

    return (
        <div>
            <div>Living Color: 
            <div style={ styles.swatch } onClick={ handleLivingClick }>
            <div style={ styles.living } />
            </div>
            { displayLivingPicker ? <div style={ styles.popover }>
            <div style={ styles.cover } onClick={ handleLivingClose }/>
            <SketchPicker color={ livingColor } onChange={ handleLivingChange } />
            </div> : null }
            </div>

            <div> Dead Color:
            <div style={ styles.swatch } onClick={ handleDeadClick }>
            <div style={ styles.dead } />
            </div>
            { displayDeadPicker ? <div style={ styles.popover }>
            <div style={ styles.cover } onClick={ handleDeadClose }/>
            <SketchPicker color={ deadColor } onChange={ handleDeadChange } />
            </div> : null }
            </div>
        </div>
    )
}
