import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { selectSpeed, setSpeed } from './speedSlice';
import { selectStart } from '../buttons/gameStateSlice';
import './speed.css';

export default function Frequency() {
    const frq = useSelector(selectSpeed);
    const start = useSelector(selectStart);
    const dispatch = useDispatch();
    return (
        <div className="speedContainer row mb-2">
            <label className="speedLabel smallerText buttonText col-sm-6" for="speed">
                Speed: {(frq / 1000).toFixed(2)} s
            </label>
            <input
            type="range"
            class="custom-range speedRange col-sm-6"
            min="50"
            max="2000"
            id="speed"
            value={frq}
            disabled={start ? 'disabled' : null}
            onChange={e => dispatch(setSpeed(e.target.value))}
            ></input>
        </div>
    );
}
