import React, {useState} from 'react'
import { useDispatch } from 'react-redux'
import {
    toggleGame,
    resetGame,
 } from './gameStateSlice'
import {
    resetGrid
 } from './gridSlice'

export default function GameButton() {
    const dispatch = useDispatch()
    // let state = "start"
    const [state, setState] = useState("start")

    function toggleState() {
        dispatch(toggleGame())
        if (state === "start") {
            return "pause"
        } 
        return "start"
    }

    function resetState() {
        // state = "start"
        dispatch(resetGame())
        dispatch(resetGrid())
        return "start"
    }


    return (
        <div>
            <button onClick={() => setState(toggleState())}>{state}</button>
            <button onClick={() => setState(resetState())}>Reset</button>
        </div>
    )
}
