import React, { useState, useEffect } from 'react';
import { useMediaQuery } from 'react-responsive';
import { useDispatch, useSelector } from 'react-redux';
import { toggleCell, selectGrid, selectWidth, selectHeight } from './gridSlice';
import { selectStart } from '../buttons/gameStateSlice';
import {
    selectDeadColor,
    selectLivingColor,
    selectHeatMap,
    selectColorGradientArray,
} from '../color/colorSlice';

function debounce(fn, ms) {
    let timer
    return _ => {
      clearTimeout(timer)
      timer = setTimeout(_ => {
        timer = null
        fn.apply(this, arguments)
      }, ms)
    };
  }

export default function Cell(props) {
    const isLG = useMediaQuery({
        query: '(min-device-width: 1200px)'
    });
    const isMD = useMediaQuery({
        query: '(min-device-width: 992px)'
    });
    const isSM = useMediaQuery({
        query: '(min-device-width: 768px)'
    });
    const isXS = useMediaQuery({
        query: '(max-device-width: 500px)'
    });
    const isPhone = useMediaQuery({
        query: '(max-device-width: 400px)'
    });


    const dispatch = useDispatch();

    const grid = useSelector(selectGrid);
    const width = useSelector(selectWidth);
    const height = useSelector(selectHeight);
    const start = useSelector(selectStart);
    const livingColor = useSelector(selectLivingColor);
    const deadColor = useSelector(selectDeadColor);
    const heatMap = useSelector(selectHeatMap);
    const colorGradientArray = useSelector(selectColorGradientArray);

    const [windowWidth, setWindowWidth] = useState(window.innerWidth);
    const [cellWidth, setCellWidth] = useState((windowWidth*0.4 / width).toFixed(2));
    const [cellHeight, setCellHeight] = useState((windowWidth*0.4 / height).toFixed(2));
    const [sizeStyle, setSizeStyle] = useState({
        width: `${cellWidth}px`,
        height: `${cellHeight}px`,
    });

    useEffect(() => {
        function handleResize() {
            setWindowWidth(window.innerWidth);
            if (isLG) {
                setCellWidth(computeCellLength(700, width));
                setCellHeight(computeCellLength(700, height));
            } else if (isMD) {
                setCellWidth(computeCellLength(600, width));
                setCellHeight(computeCellLength(600, height));
            } else if (isSM) {
                setCellWidth(computeCellLength(500, width));
                setCellHeight(computeCellLength(500, height));
            } else if (isXS) {
                setCellWidth(computeCellLength(400, width));
                setCellHeight(computeCellLength(400, height));
            } else if (isPhone) {
                setCellWidth(computeCellLength(300, width));
                setCellHeight(computeCellLength(300, height));
            }
            setSizeStyle({
                width: `${cellWidth}px`,
                height: `${cellHeight}px`,
            })
          }

        window.addEventListener('resize', handleResize);
        return _=> {
            window.removeEventListener('resize', handleResize)
        };
    });

    const computeCellLength = (gridLength, num) => (gridLength / num).toFixed(2);

    let colorStyle = {}

    function addLifeStatus() {
        if (grid[props.pos[0]][props.pos[1]] === 1) {
            colorStyle.backgroundColor = `${livingColor}`;
        } else if (!heatMap) {
            colorStyle.backgroundColor = `${deadColor}`;
        } else {
            // build heat map for dead cells
            let val = -Number(grid[props.pos[0]][props.pos[1]]);
            colorStyle.backgroundColor = `${
                colorGradientArray[Number(val + 1)]
            }`;
        }
    }
    addLifeStatus();

    function clickCell() {
        if (!start) {
            dispatch(toggleCell(props.pos));
        }
    }

    return (
        <div
            className="gridCol"
            style={sizeStyle, colorStyle}
            onClick={() => clickCell()}></div>

    );
}
