import React, { useEffect, useState } from 'react';
import { useMediaQuery } from 'react-responsive';
import { useDispatch, useSelector } from 'react-redux';
import {
    selectGrid,
    selectWidth,
    selectHeight,
    updateGridAndNeighbors,
    setCellSize,
} from './gridSlice';
import { selectStart } from '../buttons/gameStateSlice';
import { selectSpeed } from '../speed/speedSlice';
import Cell from './Cell';
import './grid.css';

export default function Grid() {
    const isLG = useMediaQuery({
        query: '(min-device-width: 1200px)'
    });
    const isMD = useMediaQuery({
        query: '(min-device-width: 992px)'
    });
    const isSM = useMediaQuery({
        query: '(min-device-width: 768px)'
    });
    const isXS = useMediaQuery({
        query: '(min-device-width: 500px)'
    });

    const setLength = () => {
        let gridLength;
        if (isLG) {
            gridLength = 700;
        } else if (isMD) {
            gridLength = 600;
        } else if (isSM) {
            gridLength = 500;
        } else if (isXS) {
            gridLength = 400;
        } else {
            gridLength = 300;
        }
        return gridLength;
    };

    const grid = useSelector(selectGrid);
    const start = useSelector(selectStart);
    const speed = useSelector(selectSpeed);
    const width = useSelector(selectWidth);
    const height = useSelector(selectHeight);
    // const cellWidth = useSelector(selectCellWidth);
    // const cellHeight = useSelector(selectCellHeight);
    // const [cellWidth, setCellWidth] = useState((setLength() / width).toFixed(2));
    // const [cellHeight, setCellHeight] = useState((setLength() / height).toFixed(2));

    const dispatch = useDispatch();

    useEffect(() => {
        const timer = setTimeout(() => {
            if (start) {
                dispatch(updateGridAndNeighbors());
            }
        }, Number(speed));
        const handleResize = () => {
            console.log("handle resize");
            // setWindowWidth(window.innerWidth);
            let gridLength = setLength();
            dispatch(setCellSize(gridLength));
            // setCellWidth(computeCellLength(gridLength, width));
            // setCellHeight(computeCellLength(gridLength, height));
            // setSizeStyle({
            //     width: `${cellWidth}px`,
            //     height: `${cellHeight}px`,
            // });
        };
        window.addEventListener('resize', handleResize);
        return _ => {
            window.removeEventListener('resize', handleResize);
            clearTimeout(timer);
        };
    });

    return (
        <div className="flexRow">
            <div className="gridContainer">
                {grid.map(function (row, rowId) {
                    return (
                        <div className="gridRow">
                            {row.map((val, colId) => (
                                <Cell pos={[rowId, colId]} />
                            ))}
                        </div>
                    );
                })}
            </div>
        </div>
    );
}
