import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    selectGrid,
    updateGridAndNeighbors,
    setCellSize,
} from './gridSlice';
import { selectStart } from '../buttons/gameStateSlice';
import { selectSpeed } from '../speed/speedSlice';
import Cell from './Cell';
import './grid.css';

export default function Grid() {
    const grid = useSelector(selectGrid);
    const start = useSelector(selectStart);
    const speed = useSelector(selectSpeed);

    const dispatch = useDispatch();

    const setLength = () => {
        const windowWidth = window.innerWidth;
        let gridLength;
        if (windowWidth >= 1200) {
            gridLength = 700;
        } else if (windowWidth >= 992) {
            gridLength = 600;
        } else if (windowWidth >= 768) {
            gridLength = 500;
        } else if (windowWidth >= 500) {
            gridLength = 400;
        } else {
            gridLength = 300;
        }
        return gridLength;
    };

    useEffect(() => {
        const timer = setTimeout(() => {
            if (start) {
                dispatch(updateGridAndNeighbors());
            }
        }, Number(speed));
        const handleResize = () => {
            let gridLength = setLength();
            dispatch(setCellSize(gridLength));
        };
        window.addEventListener('resize', handleResize);
        return _ => {
            window.removeEventListener('resize', handleResize);
            clearTimeout(timer);
        };
    });

    return (
        <div className="flexRow">
            <div className="gridContainer">
                {grid.map(function (row, rowId) {
                    return (
                        <div className="gridRow">
                            {row.map((val, colId) => (
                                <Cell pos={[rowId, colId]} />
                            ))}
                        </div>
                    );
                })}
            </div>
        </div>
    );
}
