import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { selectCount, selectStage } from '../grid/gridSlice';
import { stopGame } from '../buttons/gameStateSlice';
import './counter.css';

export default function Counter() {
    const count = Number(useSelector(selectCount));
    const stage = Number(useSelector(selectStage));
    const dispatch = useDispatch();
    let countWord = '';
    let stageWord = '';
    countWord = 'Living cells: ' + count;
    stageWord = 'stage: ' + stage;

    return (
        <div className="counterRow row mb-2">
            <span className="counterItem col-6 col-md-8">{countWord}</span>
            <span className="counterItem col-6 col-md-4">{stageWord}</span>
        </div>
    );
}
