import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {
    selectSpeed,
    setSpeed,
} from './speedSlice'
import {
    selectStart
} from './gameStateSlice'
import Form from 'react-bootstrap/Form'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

export default function Frequency() {
    const frq = useSelector(selectSpeed)
    const start = useSelector(selectStart)
    const dispatch = useDispatch()
    return (
        <div>
            <Form>
                <Form.Group as={Row} controlId="forSpeed">
                    <Form.Label column sm="4">
                        Update Speed: {(frq/1000).toFixed(2)} s
                    </Form.Label>
                    <Col sm="8">
                        <Form.Control className="speedRange" type="range" min="50" max="2000"
                        id="speed" name="speedRange" value={frq}
                        disabled={start ? "disabled" : null}
                        onChange={e => dispatch(setSpeed(e.target.value))}/>
                    </Col>
                </Form.Group>
            </Form>
