import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {toggleCell} from './gridSlice'

// import Col from 'react-bootstrap/Col'

export default function Cell(props) {
    const dispatch = useDispatch()
    let className = "col"
    function addLifeStatus() {
        if (props.life === 1) {
            className += ' living'
        } else {
            className += ' dead'
        }
    }
    addLifeStatus();

    return (
        <div className={className} onClick={()=>dispatch({type:'grid/toggleCell', payload: props.pos})}></div>
    )
}
