import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    toggleGame,
    stopGame,
    selectPlay,
    selectStart,
    selectPlaySign,
} from './gameStateSlice';
import {
    resetGrid,
    selectCount,
    updateGridAndNeighbors,
    clearGrid,
} from '../grid/gridSlice';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    faForward,
    faTimes,
    faRandom,
} from '@fortawesome/free-solid-svg-icons';

export default function GameButton() {
    const dispatch = useDispatch();
    const play = useSelector(selectPlay);
    const start = useSelector(selectStart);
    const count = useSelector(selectCount);
    const playSign = useSelector(selectPlaySign);

    const toggleState = () => {
        if (count > 0) {
            dispatch(toggleGame());
        }
    };

    const resetState = () => {
        // state = "start"
        dispatch(stopGame());
        dispatch(resetGrid());
    };

    const handleNext = () => {
        if (!start) {
            dispatch(updateGridAndNeighbors());
        }
    };

    const handleClear = () => {
        dispatch(stopGame());
        dispatch(clearGrid());
    };

    return (
        <div className="gameButtonRow row">
            <div className="col-3">
                <div className="gameButtonContainer">
                    <Button
                        variant="outline-dark"
                        className="gameStateButton"
                        size="sm"
                        onClick={toggleState}>
                        <FontAwesomeIcon
                            className="fontAwesomeIcon"
                            icon={playSign}
                        />
                        {play}
                    </Button>
                </div>
            </div>
            <div className="col-3">
                <div className="gameButtonContainer">
                    <Button
                        variant="outline-dark"
                        className="gameStateButton"
                        size="sm"
                        onClick={handleNext}>
                        <FontAwesomeIcon
                            className="fontAwesomeIcon"
                            icon={faForward}
                        />
                        Next
                    </Button>
                </div>
            </div>
            <div className="col-3">
                {/* <div className="gameButtonContainer"> */}
                    <Button
                        variant="outline-dark"
                        className="gameStateButton"
                        size="sm"
                        onClick={resetState}>
                        <FontAwesomeIcon
                            className="fontAwesomeIcon"
                            icon={faRandom}
                        />
                        Reset
                    </Button>
                {/* </div> */}
            </div>
            <div className="col-3">
                <div className="gameButtonContainer">
                    <Button
                        variant="outline-dark"
                        className="gameStateButton"
                        size="sm"
                        onClick={handleClear}>
                        <FontAwesomeIcon
                            className="fontAwesomeIcon"
                            icon={faTimes}
                        />
                        Clear
                    </Button>
                </div>
            </div>
        </div>
    );
}
