import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {
    toggleCell,
    selectGrid,
    selectWidth,
    selectHeight,
} from './gridSlice'
import {
    selectStart
} from './gameStateSlice'
import {
    selectDeadColor,
    selectLivingColor,
    selectHeatMap,
    selectColorGradientArray
} from './colorSlice'


export default function Cell(props) {
    const dispatch = useDispatch()
    const grid = useSelector(selectGrid)
    const width = useSelector(selectWidth)
    const height = useSelector(selectHeight)
    const start = useSelector(selectStart)
    const livingColor = useSelector(selectLivingColor)
    const deadColor = useSelector(selectDeadColor)
    const heatMap = useSelector(selectHeatMap)
    const colorGradientArray = useSelector(selectColorGradientArray)
    const squareLength = Math.min((500/width).toFixed(2), (500/height).toFixed(2))

    let colorStyle = {
        width: `${squareLength}px`,
        height: `${squareLength}px`,
    }

    function addLifeStatus() {
        if (grid[props.pos[0]][props.pos[1]] === 1) {
            colorStyle.backgroundColor = `${ livingColor }`
        } else if (!heatMap) {
            colorStyle.backgroundColor = `${ deadColor }`
        } else {
            // build heat map for dead cells
            let val = - Number(grid[props.pos[0]][props.pos[1]])
            colorStyle.backgroundColor = `${colorGradientArray[Number(val+1)]}`
        }
    }
    addLifeStatus();

    function clickCell() {
        if (!start) {
            dispatch(toggleCell(props.pos))
        }
    }

    return (
        <div className = "gridCol" style={colorStyle} onClick={() => clickCell()}>
        </div>
    )
}
