import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    selectGrid,
    selectWidth,
    selectHeight,
    updateGridAndNeighbors,
    setCellHeight,
    setCellWidth,
} from './gridSlice';
import { selectStart } from '../buttons/gameStateSlice';
import { selectSpeed } from '../speed/speedSlice';
import Cell from './Cell';
import './grid.css';

export default function Grid() {
    const grid = useSelector(selectGrid);
    const start = useSelector(selectStart);
    const speed = useSelector(selectSpeed);
    const width = useSelector(selectWidth);
    const height = useSelector(selectHeight);
    // const cellWidth = useSelector(selectCellWidth);
    // const cellHeight = useSelector(selectCellHeight);
    // const [cellWidth, setCellWidth] = useState((setLength() / width).toFixed(2));
    // const [cellHeight, setCellHeight] = useState((setLength() / height).toFixed(2));

    const dispatch = useDispatch();

    useEffect(() => {
        const timer = setTimeout(() => {
            if (start) {
                dispatch(updateGridAndNeighbors());
            }
        }, Number(speed));
        const handleResize = () => {
            console.log("handle resize");
            // setWindowWidth(window.innerWidth);
            // let gridLength = setLength();
            setCellWidth(computeCellLength(gridLength, width));
            setCellHeight(computeCellLength(gridLength, height));
            setSizeStyle({
                width: `${cellWidth}px`,
                height: `${cellHeight}px`,
            });
            // if (isLG) {
            //     setCellWidth(computeCellLength(700, width));
            //     setCellHeight(computeCellLength(700, height));
            // } else if (isMD) {
            //     setCellWidth(computeCellLength(600, width));
            //     setCellHeight(computeCellLength(600, height));
            // } else if (isSM) {
            //     setCellWidth(computeCellLength(500, width));
            //     setCellHeight(computeCellLength(500, height));
            // } else if (isXS) {
            //     setCellWidth(computeCellLength(400, width));
            //     setCellHeight(computeCellLength(400, height));
            // } else {
            //     setCellWidth(computeCellLength(300, width));
            //     setCellHeight(computeCellLength(300, height));
            // }
            // setSizeStyle({
            //     width: `${cellWidth}px`,
            //     height: `${cellHeight}px`,
            // });
        };
        window.addEventListener('resize', handleResize);
        return _ => {
            window.removeEventListener('resize', handleResize);
            clearTimeout(timer);
        };
    });

    return (
        <div className="flexRow">
            <div className="gridContainer">
                {grid.map(function (row, rowId) {
                    return (
                        <div className="gridRow">
                            {row.map((val, colId) => (
                                <Cell pos={[rowId, colId]} sizeStyle={sizeStyle}/>
                            ))}
                        </div>
                    );
                })}
            </div>
        </div>
    );
}
