'use strict'

import React, {useState} from 'react'
import reactCSS from 'reactcss'
import { SketchPicker } from 'react-color'
import { useDispatch, useSelector } from 'react-redux'
import {
    setDeadColor,
    selectLivingColor,
} from './colorSlice'


export default function Color() {
    const [displayColorPicker, setDisplay] = useState(false)
    const [livingColor, setLivingColor] = useState({r: '0', g: '0', b: '0', a: '1',})
    // const [livingColor, setLivingColor] = useState({r: '0', g: '0', b: '0', a: '1',})
    const [deadColor, setDeadColor] = useState({r: '255', g: '255', b: '255', a: '1',})

    const styles = reactCSS({
        'default': {
          living: {
            width: '36px',
            height: '14px',
            borderRadius: '2px',
            background: `rgba(${ livingColor.r }, ${ livingColor.g }, ${ livingColor.b }, ${ livingColor.a })`,
          },
          dead: {
            width: '36px',
            height: '14px',
            borderRadius: '2px',
            background: `rgba(${ deadColor.r }, ${ deadColor.g }, ${ deadColor.b }, ${ deadColor.a })`,
          },
          swatch: {
            padding: '5px',
            background: '#fff',
            borderRadius: '1px',
            boxShadow: '0 0 0 1px rgba(0,0,0,.1)',
            display: 'inline-block',
            cursor: 'pointer',
          },
          popover: {
            position: 'absolute',
            zIndex: '2',
          },
          cover: {
            position: 'fixed',
            top: '0px',
            right: '0px',
            bottom: '0px',
            left: '0px',
          },
        },
      });

    const handleClick = () => {
        setDisplay(!displayColorPicker)
    };

    const handleClose = () => {
        setDisplay(false)
    };

    const handleLivingChange = (color) => {
        setLivingColor(color.rgb)
    };

    const handleDeadChange = (color) => {
        setDeadColor(color.rgb)
    };

    // const handleChange = (color, type) => {
    //     if (type === "living") {
    //         setLivingColor(color.rgb)
    //     } else {
    //         setDeadColor(color.rgb)
    //     }
    //     // setColor(color.rgb)
    // };

    return (
        <div>
            <div style={ styles.swatch } onClick={ handleClick }>
            <div style={ styles.living } />
            </div>
            { displayColorPicker ? <div style={ styles.popover }>
            <div style={ styles.cover } onClick={ handleClose }/>
            <SketchPicker color={ livingColor } onChange={ handleLivingChange } />
            </div> : null }

            <div style={ styles.swatch } onClick={ handleClick }>
            <div style={ styles.dead } />
            </div>
            { displayColorPicker ? <div style={ styles.popover }>
            <div style={ styles.cover } onClick={ handleClose }/>
            <SketchPicker color={ deadColor } onChange={ handleDeadChange } />
            </div> : null }
        </div>

            // {/* <div style={ styles.swatch } onClick={ handleClick }>
            // <div style={ {...styles.color, ...styles.dead} } />
            // </div>
            // { displayColorPicker ? <div style={ styles.popover }>
            // <div style={ styles.cover } onClick={ handleClose }/>
            // <SketchPicker color={ deadColor } onChange={ handleChange(deadColor, "dead") } />
            // </div> : null } */}
        // </div>
    )
}
