import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {toggleCell,
    selectGrid} from './gridSlice'
import {
    selectStart
} from './gameStateSlice'
import {
    selectDeadColor,
    selectLivingColor,
} from './colorSlice'
// import Col from 'react-bootstrap/Col'

export default function Cell(props) {
    const dispatch = useDispatch()
    const grid = useSelector(selectGrid)
    const start = useSelector(selectStart)
    const livingColor = useSelector(selectLivingColor)
    const deadColor = useSelector(selectDeadColor)

    let colorStyle = {}
    // let className = ""
    function addLifeStatus() {
        // className = "col"
        if (grid[props.pos[0]][props.pos[1]] === 1) {
            colorStyle.backgroundColor = `rgba(${ livingColor.r }, ${ livingColor.g }, ${ livingColor.b }, ${ livingColor.a })`
            // className += ' living'
        } else {
            colorStyle.backgroundColor = `rgba(${ deadColor.r }, ${ deadColor.g }, ${ deadColor.b }, ${ deadColor.a })`
            // className += ' dead'
        }
    }
    addLifeStatus();

    function clickCell() {
        if (!start) {
            dispatch(toggleCell(props.pos))
        }
    }

    return (
        <div className = "col" style={colorStyle} onClick={() => clickCell()}>
        </div>
    )
}
