import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    toggleGame,
    stopGame,
    selectPlay,
    selectStart,
    selectPlaySign,
} from './gameStateSlice';
import {
    resetGrid,
    selectCount,
    updateGridAndNeighbors,
    clearGrid,
} from '../grid/gridSlice';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    faForward,
    faTimes,
    faRandom,
} from '@fortawesome/free-solid-svg-icons';
import './buttons.css';

export default function GameButton() {
    const dispatch = useDispatch();
    const play = useSelector(selectPlay);
    const start = useSelector(selectStart);
    const count = useSelector(selectCount);
    const playSign = useSelector(selectPlaySign);

    const toggleState = () => {
        if (count > 0) {
            dispatch(toggleGame());
        }
    };

    const resetState = () => {
        // state = "start"
        dispatch(stopGame());
        dispatch(resetGrid());
    };

    const handleNext = () => {
        if (!start) {
            dispatch(updateGridAndNeighbors());
        }
    };

    const handleClear = () => {
        dispatch(stopGame());
        dispatch(clearGrid());
    };

    return (
        <div className="gameButtonRow row mb-2">
            <div>
                <Button
                    variant="outline-dark"
                    className="gameStateButton col-sm-3"
                    size="sm"
                    onClick={toggleState}>
                    <FontAwesomeIcon className="fontAwesomeIcon" icon={playSign} />
                    {play}
                </Button>
            </div>
            <Button
                variant="outline-dark"
                className="gameStateButton col-sm-3"
                size="sm"
                onClick={handleNext}>
                <FontAwesomeIcon className="fontAwesomeIcon" icon={faForward} />
                Next
            </Button>
            <Button
                variant="outline-dark"
                className="gameStateButton col-sm-3"
                size="sm"
                onClick={resetState}>
                <FontAwesomeIcon className="fontAwesomeIcon" icon={faRandom} />
                Reset
            </Button>
            <Button
                variant="outline-dark"
                className="gameStateButton col-sm-3"
                size="sm"
                onClick={handleClear}>
                <FontAwesomeIcon className="fontAwesomeIcon" icon={faTimes} />
                Clear
            </Button>
        </div>
    );
}
