import React, { useEffect, useState } from 'react';
// import Container from 'react-bootstrap/Container'
// import Row from 'react-bootstrap/Row'
// import Col from 'react-bootstrap/Col'
import { useDispatch, useSelector } from 'react-redux';
import {
    selectGrid,
    updateGridAndNeighbors,
    initializeGridAndNeighbors,
} from './gridSlice';
import { selectStart } from './gameStateSlice';
import { selectSpeed } from './speedSlice';
import Cell from './Cell';

export default function Grid() {
    const grid = useSelector(selectGrid);
    const start = useSelector(selectStart);
    const speed = useSelector(selectSpeed);
    const dispatch = useDispatch();
    // initialization
    if (grid.length === 0) {
        dispatch(initializeGridAndNeighbors());
    }
    useEffect(() => {
        const timer = setTimeout(() => {
            if (start) {
                dispatch(updateGridAndNeighbors());
            }
        }, Number(speed));
        return () => clearTimeout(timer);
    });

    return (
        <div className="gridContainer">
            {grid.map(function (row, rowId) {
                return (
                    <div className="gridRow">
                        {row.map((val, colId) => (
                            <Cell pos={[rowId, colId]} />
                        ))}
                    </div>
                );
            })}
        </div>
    );
}
